#LandmarkCalculations.Rlandmarks=ReadAmiraLandmarks(file.path(ExamplesDir,"Calculating an Affine Registration from Landmarks","Template2FemaleAverageGoodBrains.landmarkAscii"))landmarksAffineTransform6Dof=CalculateIGSParamsFromLandmarkPairs(landmarks,dofs=6,Swap=T)AffineTransform9Dof=CalculateIGSParamsFromLandmarkPairs(landmarks,dofs=9,AffineTransform6Dof,Swap=T)AffineTransform9DofAffineMatrix9Dof=ComposeAffineFromIGSParams(AffineTransform9Dof)AffineMatrix9DofTransformPoints(landmarks[[2]], AffineMatrix9Dof)landmarks[[1]]TransformPoints(landmarks[[1]], solve(AffineMatrix9Dof))landmarks[[2]]write.table(AffineMatrix9Dof,file="~/Desktop/Affine.mat",row.names=F,col.names=F,sep="\t")igsreg=IGSParamsToIGSRegistration(AffineTransform9Dof)WriteIGSRegistrationFolder(igsreg,"testaffinereg")WriteIGSTypedStr(igsreg,"testaffinereg/registration")